Usage Example: //Createloggerwithdefaultconfiguration
EspLoggerlogger;

//Initializewithcustomconfiguration
hf_logger_config_tconfig={};
config.default_level=hf_log_level_t::LOG_LEVEL_INFO;
config.output_destination=hf_log_output_t::LOG_OUTPUT_UART;
config.format_options=hf_log_format_t::LOG_FORMAT_DEFAULT;
config.max_message_length=512;
config.buffer_size=1024;
config.enable_thread_safety=true;
config.enable_performance_monitoring=true;

if(logger.Initialize(config)==hf_logger_err_t::LOGGER_SUCCESS){
logger.Info("MAIN","Systeminitializedsuccessfully");
logger.Debug("SENSOR","Temperature:%.2fC",temperature);
logger.Error("COMM","Communicationtimeout");

//LogV2features(ifavailable)
uint8_tbuffer[16]={0x01,0x02,0x03,0x04};
logger.LogBuffer("DATA",buffer,sizeof(buffer),hf_log_level_t::LOG_LEVEL_DEBUG);
}

Advanced Usage with Log V2: //Dynamicformatstrings(LogV2feature)
constchar*dynamic_format="Dynamicmessage:%swithvalue%d";
logger.Log(hf_log_level_t::LOG_LEVEL_INFO,"TAG",dynamic_format,"test",42);

//Binarylogging
uint8_tbinary_data[64];
//...fillbinary_data...
logger.LogBufferHex("BINARY",binary_data,sizeof(binary_data),hf_log_level_t::LOG_LEVEL_DEBUG);
logger.LogBufferChar("TEXT",(constchar*)binary_data,sizeof(binary_data),
hf_log_level_t::LOG_LEVEL_INFO);
